var name1 = "My GPS Device 1";
var type1 = "GPS";
var longitude1 = [-98.49,-97.74,-96.79,-94.20,-90.19,-94.57,-87.62,-87.90,-93.26,-95.99];
var latitude1 = [29.42,30.26,32.77,36.37,38.62,39.09,41.87,43.03,44.97,41.25];
var velocity1 = context.get('velocity1')||0;



velocity1 = velocity1+1;
if(velocity1 > 30) velocity1=0;
context.set('velocity1',velocity1);

var counter1 = context.get('counter1')||0;
counter1 = counter1+1;
if(counter1 > 9) counter1 = 0;
context.set('counter1',counter1);

msg = {
 	payload: JSON.stringify(
 	 { d:{
 "name" : name1,
 "location" : 
 {
 "longitude" : longitude1[counter1],
 	 "latitude" : latitude1[counter1]
 },
 "velocity" : velocity1,
 	 "type" : type1
 	 }
 	 }
 )
}

return msg;

